import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {Observable} from "rxjs";
import {ActionEvent, AppDataState, DataStateEnum, ProductActionsTypes} from "../../../State/product.state";
import {Product} from "../../../model/product.model";

@Component({
  selector: 'app-products-list',
  templateUrl: './products-list.component.html',
  styleUrls: ['./products-list.component.css']
})
export class ProductsListComponent implements OnInit {

  @Input() productsInput$:Observable<AppDataState<Product[]>> |null=null;
  //@Output() productsEventEmitter:EventEmitter<ActionEvent> =new EventEmitter<ActionEvent>();
  readonly DataStateEnum=DataStateEnum;
  constructor() { }

  ngOnInit(): void {
  }

/*  onSelect(p: Product) {
  this.productsEventEmitter.emit({type:ProductActionsTypes.SELECT_PRODUCT,payload:p});
  }

  onDelete(p: Product) {
    this.productsEventEmitter.emit({type:ProductActionsTypes.DELETE_PRODUCT,payload:p});
  }

  onEdit(p: Product) {
    this.productsEventEmitter.emit({type:ProductActionsTypes.EDIT_PRODUCT,payload:p});
  }

    onActionEvent($event: ActionEvent) {
    this.productsEventEmitter.emit($event);
  }*/
}
<div class="container">
  <ng-container *ngIf="(productsInput$ | async) as result" [ngSwitch]="result.dataState" >
    <ng-container *ngSwitchCase="DataStateEnum.LOADING">
      Loading...
    </ng-container>
    <ng-container *ngSwitchCase="DataStateEnum.ERROR">
      <div class="alert-danger">
        {{result.errorMessage}}
      </div>
    </ng-container>
    <ng-container *ngSwitchCase="DataStateEnum.LOADED">
      <table class="table">
        <tr>
          <th>ID</th><th>Name</th><th>Price</th><th>Quantity</th><th>Selected</th><th>Available</th>
        </tr>
        <app-product-item
          [product]="p"
          *ngFor="let p of result.data" style="display: contents">

        </app-product-item>

      </table>
    </ng-container>
  </ng-container>
</div>
